function generate_menu_links()
{
	if (have_rows('links_menu', 'options')) {
		$total_rows = count(get_field('links_menu', 'options'));
		$counter = 1;

		while (have_rows('links_menu', 'options')) {
			the_row();

			$link = get_sub_field('link', 'options');

			if ($link) {
				$link_url = $link['url'];
				$link_title = $link['title'];
				$link_target = $link['target'] ? $link['target'] : '_self';

				$id = strtolower(str_replace(' ', '-', $link_title));

				$class = $counter === $total_rows ? 'btn-menu-navlink last-item' : 'btn-menu-navlink';

				if (!empty($link_title)) {
					echo '<li id="' . esc_attr($id) . '" class="' . esc_attr($class) . '">';

					// Verifica se o ID é "jeito-due" e ajusta o <a> conforme necessário
					if ($id === 'jeito-due') {
						echo '<a class="button-menu ' . 'gtm-' . esc_attr($id) . '-banner' . '">';
					} else {
						echo '<a class="button-menu ' . 'gtm-' . esc_attr($id) . '-banner' . '" href="' . esc_url($link_url) . '" target="' . esc_attr($link_target) . '">';
					}

					// Adiciona o texto do link
					echo esc_html($link_title);

					// Adiciona o SVG dentro de uma tag <i> se o ID for "jeito-due"
					if ($id === 'jeito-due') {
						echo '<i>
								<svg class="rotate-svg" xmlns="http://www.w3.org/2000/svg" width="12" height="6" viewBox="0 0 12 6" fill="none">
									<path d="M0.999023 0.5L5.99902 5.5L10.999 0.5" stroke="#fff" stroke-width="0.714286"/>
								</svg>
							  </i>';
					}

					// Fecha a tag <a> e <li>
					echo '</a>';
					echo '</li>';
				}
			}
			$counter++;
		}
	}
}
