function generate_navbar()
{
	echo '<div class="box-navbar-numeros">';
	echo '<div class="box-numeros">';

	if (have_rows('numeros_repetidor', 'options')) {
		// Inicialize o contador fora do loop
		$counter = 1;

		while (have_rows('numeros_repetidor', 'options')) {
			the_row();

			$link = get_sub_field('numeros_menu_lateral', 'options');

			if ($link) {
				$link_url = $link['url'];
				$link_title = $link['title'];
				$link_target = $link['target'] ? $link['target'] : '_self';

				// Defina a classe de acordo com o contador
				$class = '';
				if ($counter === 1) {
					$class = 'gtm-numero-whats-navbar';
				} elseif ($counter === 2) {
					$class = 'gtm-numero-telefone-banner';
				} else {
					$class = 'gtm-numero-sidebar';
				}

				if (!empty($link_url) && !empty($link_title)) {
					echo '<li class="link-numero">';
					echo '<a class="button-menu-numero ' . esc_attr($class) . '" href="' . esc_url($link_url) . '" target="' . esc_attr($link_target) . '">';

					// Adiciona o SVG dentro do link
					$svg_file = get_sub_field('svg_numeros', 'options');
					if ($svg_file && pathinfo($svg_file['url'], PATHINFO_EXTENSION) === 'svg') {
						echo '<i class="element">';
						echo file_get_contents($svg_file['url']);
